CREATE OR REPLACE FUNCTION public.delete_user_account(user_id uuid)
 RETURNS boolean
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
BEGIN
  -- Delete from profiles table first (due to foreign key constraints)
  DELETE FROM profiles WHERE id = user_id;
  
  -- Delete from certificates table if username matches
  DELETE FROM certificates 
  WHERE maximally_username IN (
    SELECT username FROM profiles WHERE id = user_id
  );
  
  -- Delete the user from auth.users (requires admin privileges)
  DELETE FROM auth.users WHERE id = user_id;
  
  RETURN TRUE;
EXCEPTION
  WHEN OTHERS THEN
    -- Log the error and return false
    RAISE LOG 'Error deleting user %: %', user_id, SQLERRM;
    RETURN FALSE;
END;
$function$

CREATE OR REPLACE FUNCTION public.generate_registration_number()
 RETURNS text
 LANGUAGE plpgsql
AS $function$
DECLARE
  reg_number TEXT;
  exists_check INTEGER;
BEGIN
  LOOP
    reg_number := 'REG-' || UPPER(SUBSTRING(MD5(RANDOM()::TEXT) FROM 1 FOR 8));
    SELECT COUNT(*) INTO exists_check FROM hackathon_registrations WHERE registration_number = reg_number;
    EXIT WHEN exists_check = 0;
  END LOOP;
  RETURN reg_number;
END;
$function$

CREATE OR REPLACE FUNCTION public.generate_submission_slug(p_project_name text, p_submission_id integer)
 RETURNS text
 LANGUAGE plpgsql
AS $function$
DECLARE
  v_slug TEXT;
  v_counter INTEGER := 0;
  v_final_slug TEXT;
BEGIN
  -- Generate base slug from project name
  v_slug := LOWER(REGEXP_REPLACE(p_project_name, '[^a-zA-Z0-9]+', '-', 'g'));
  v_slug := TRIM(BOTH '-' FROM v_slug);
  
  -- If slug is empty, use submission id
  IF v_slug = '' OR v_slug IS NULL THEN
    v_slug := 'submission-' || p_submission_id;
  END IF;
  
  -- Ensure uniqueness
  v_final_slug := v_slug;
  WHILE EXISTS (SELECT 1 FROM public.hackathon_submissions WHERE slug = v_final_slug AND id != p_submission_id) LOOP
    v_counter := v_counter + 1;
    v_final_slug := v_slug || '-' || v_counter;
  END LOOP;
  
  RETURN v_final_slug;
END;
$function$

CREATE OR REPLACE FUNCTION public.generate_team_code()
 RETURNS text
 LANGUAGE plpgsql
AS $function$
DECLARE
  team_code TEXT;
  exists_check INTEGER;
BEGIN
  LOOP
    team_code := UPPER(SUBSTRING(MD5(RANDOM()::TEXT) FROM 1 FOR 6));
    SELECT COUNT(*) INTO exists_check FROM hackathon_teams WHERE team_code = team_code;
    EXIT WHEN exists_check = 0;
  END LOOP;
  RETURN team_code;
END;
$function$

