CREATE OR REPLACE FUNCTION public.log_hackathon_status_change_activity()
 RETURNS trigger
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
  activity_action TEXT;
  activity_severity TEXT;
BEGIN
  -- Only log if status actually changed
  IF OLD.status = NEW.status THEN
    RETURN NEW;
  END IF;

  -- Determine action and severity based on new status
  CASE NEW.status
    WHEN 'published' THEN
      activity_action := 'Hackathon published';
      activity_severity := 'info';
    WHEN 'pending_review' THEN
      activity_action := 'Hackathon submitted for review';
      activity_severity := 'info';
    WHEN 'rejected' THEN
      activity_action := 'Hackathon rejected';
      activity_severity := 'warning';
    WHEN 'unpublished' THEN
      activity_action := 'Hackathon unpublished';
      activity_severity := 'warning';
    WHEN 'ended' THEN
      activity_action := 'Hackathon ended';
      activity_severity := 'info';
    ELSE
      activity_action := 'Hackathon status changed to ' || NEW.status;
      activity_severity := 'info';
  END CASE;

  INSERT INTO public.admin_activity_feed (
    activity_type,
    actor_id,
    actor_username,
    actor_email,
    target_type,
    target_id,
    target_name,
    action,
    metadata,
    severity
  ) VALUES (
    'hackathon_' || NEW.status,
    NULL,
    NULL, -- no organizer_name column exists
    NEW.organizer_email,
    'hackathon',
    NEW.id::text,
    NEW.hackathon_name,
    activity_action,
    jsonb_build_object(
      'old_status', OLD.status,
      'new_status', NEW.status,
      'organizer_id', NEW.organizer_id
    ),
    activity_severity
  );
  RETURN NEW;
END;
$function$

CREATE OR REPLACE FUNCTION public.record_team_collaboration()
 RETURNS trigger
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
  member RECORD;
  other_member RECORD;
  hackathon_record RECORD;
  team_record RECORD;
BEGIN
  SELECT * INTO team_record FROM hackathon_teams WHERE id = NEW.team_id;
  SELECT * INTO hackathon_record FROM organizer_hackathons WHERE id = NEW.hackathon_id;
  
  IF team_record IS NULL OR hackathon_record IS NULL THEN
    RETURN NEW;
  END IF;
  
  FOR member IN 
    SELECT hr.user_id::uuid as uid, hr.team_role, p.full_name
    FROM hackathon_registrations hr
    LEFT JOIN profiles p ON p.id = hr.user_id::uuid
    WHERE hr.team_id = NEW.team_id AND hr.user_id IS NOT NULL
  LOOP
    FOR other_member IN 
      SELECT hr.user_id::uuid as uid, hr.team_role, p.full_name
      FROM hackathon_registrations hr
      LEFT JOIN profiles p ON p.id = hr.user_id::uuid
      WHERE hr.team_id = NEW.team_id 
      AND hr.user_id IS NOT NULL
      AND hr.user_id::uuid != member.uid
    LOOP
      INSERT INTO team_collaboration_history (
        user_id, teammate_id, hackathon_id, team_id, 
        team_name, hackathon_name, team_role, teammate_role
      )
      VALUES (
        member.uid, other_member.uid, NEW.hackathon_id, NEW.team_id,
        team_record.team_name, hackathon_record.hackathon_name,
        member.team_role, other_member.team_role
      )
      ON CONFLICT (user_id, teammate_id, hackathon_id) DO NOTHING;
    END LOOP;
  END LOOP;
  
  RETURN NEW;
END;
$function$

CREATE OR REPLACE FUNCTION public.send_message_to_judges(message_id_param integer)
 RETURNS TABLE(success boolean, message text, recipients_count integer)
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
    v_message RECORD;
    v_judge RECORD;
    v_count INTEGER := 0;
BEGIN
    -- Get the message details
    SELECT * INTO v_message
    FROM public.judge_messages
    WHERE id = message_id_param;
    
    IF NOT FOUND THEN
        RETURN QUERY SELECT false, 'Message not found', 0;
        RETURN;
    END IF;
    
    -- Create recipients based on recipient_type
    IF v_message.recipient_type = 'all' THEN
        -- Send to all judges
        FOR v_judge IN 
            SELECT id, username FROM public.judges WHERE is_published = true
        LOOP
            INSERT INTO public.judge_message_recipients (
                message_id,
                judge_username,
                judge_id
            ) VALUES (
                message_id_param,
                v_judge.username,
                v_judge.id
            ) ON CONFLICT DO NOTHING;
            v_count := v_count + 1;
        END LOOP;
        
    ELSIF v_message.recipient_type = 'tier' THEN
        -- Send to judges of specific tier
        FOR v_judge IN 
            SELECT id, username FROM public.judges 
            WHERE is_published = true 
            AND tier = (v_message.recipient_filter->>'tier')::TEXT
        LOOP
            INSERT INTO public.judge_message_recipients (
                message_id,
                judge_username,
                judge_id
            ) VALUES (
                message_id_param,
                v_judge.username,
                v_judge.id
            ) ON CONFLICT DO NOTHING;
            v_count := v_count + 1;
        END LOOP;
        
    ELSIF v_message.recipient_type = 'specific' THEN
        -- Send to specific judges (from recipient_filter)
        FOR v_judge IN 
            SELECT id, username FROM public.judges 
            WHERE username = ANY(
                SELECT jsonb_array_elements_text(v_message.recipient_filter->'usernames')
            )
        LOOP
            INSERT INTO public.judge_message_recipients (
                message_id,
                judge_username,
                judge_id
            ) VALUES (
                message_id_param,
                v_judge.username,
                v_judge.id
            ) ON CONFLICT DO NOTHING;
            v_count := v_count + 1;
        END LOOP;
    END IF;
    
    -- Update message status and counts
    UPDATE public.judge_messages
    SET 
        status = 'sent',
        sent_at = NOW(),
        total_sent = v_count,
        recipient_count = v_count
    WHERE id = message_id_param;
    
    RETURN QUERY SELECT true, 'Message sent successfully', v_count;
END;
$function$

CREATE OR REPLACE FUNCTION public.send_message_to_organizers(message_id_param integer)
 RETURNS jsonb
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
  msg RECORD;
  org RECORD;
  v_recipient_count INTEGER := 0;
  tier_filter TEXT;
BEGIN
  SELECT * INTO msg FROM organizer_messages WHERE id = message_id_param;
  
  IF NOT FOUND THEN
    RETURN jsonb_build_object('success', false, 'error', 'Message not found');
  END IF;
  
  IF msg.recipient_type = 'all' THEN
    FOR org IN 
      SELECT p.id as profile_id, p.username 
      FROM organizer_profiles op
      INNER JOIN profiles p ON p.id::TEXT = op.user_id::TEXT
      WHERE p.role = 'organizer'
    LOOP
      INSERT INTO organizer_message_recipients (message_id, organizer_user_id, organizer_username)
      VALUES (message_id_param, org.profile_id, org.username)
      ON CONFLICT (message_id, organizer_username) DO NOTHING;
      v_recipient_count := v_recipient_count + 1;
    END LOOP;
    
  ELSIF msg.recipient_type = 'tier' THEN
    tier_filter := msg.recipient_filter->>0;
    
    IF tier_filter IS NULL OR tier_filter = '' THEN
      RETURN jsonb_build_object('success', false, 'error', 'Tier filter is required for tier-based messages');
    END IF;
    
    FOR org IN 
      SELECT p.id as profile_id, p.username 
      FROM organizer_profiles op
      INNER JOIN profiles p ON p.id::TEXT = op.user_id::TEXT
      WHERE p.role = 'organizer' 
        AND op.tier = tier_filter
    LOOP
      INSERT INTO organizer_message_recipients (message_id, organizer_user_id, organizer_username)
      VALUES (message_id_param, org.profile_id, org.username)
      ON CONFLICT (message_id, organizer_username) DO NOTHING;
      v_recipient_count := v_recipient_count + 1;
    END LOOP;
  END IF;
  
  UPDATE organizer_messages 
  SET 
    status = 'sent',
    sent_at = NOW(),
    recipient_count = v_recipient_count,
    total_sent = v_recipient_count,
    updated_at = NOW()
  WHERE id = message_id_param;
  
  RETURN jsonb_build_object('success', true, 'recipients_count', v_recipient_count);
END;
$function$

