CREATE OR REPLACE FUNCTION public.submit_judge_score(p_token text, p_submission_id integer, p_score numeric, p_notes text DEFAULT NULL::text)
 RETURNS jsonb
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
  v_auth record;
  v_result jsonb;
BEGIN
  -- Authenticate the token
  SELECT * INTO v_auth FROM public.authenticate_judge_token(p_token);
  
  IF NOT v_auth.is_valid THEN
    RETURN jsonb_build_object(
      'success', false,
      'error', v_auth.error_message
    );
  END IF;
  
  -- Validate score range
  IF p_score < 0 OR p_score > 10 THEN
    RETURN jsonb_build_object(
      'success', false,
      'error', 'Score must be between 0 and 10'
    );
  END IF;
  
  -- Verify submission belongs to the hackathon
  IF NOT EXISTS (
    SELECT 1 FROM public.hackathon_submissions
    WHERE id = p_submission_id AND hackathon_id = v_auth.hackathon_id
  ) THEN
    RETURN jsonb_build_object(
      'success', false,
      'error', 'Submission not found in this hackathon'
    );
  END IF;
  
  -- Insert or update score
  INSERT INTO public.judge_scores (
    hackathon_id,
    judge_id,
